import{_ as t,o as e,c as l,V as n}from"./chunks/framework.55251076.js";const u=JSON.parse('{"title":"特定单元-合成氨反应系统","description":"","frontmatter":{},"headers":[],"relativePath":"AmmoniaSynthesisProcess/Hands-on-7.md","filePath":"AmmoniaSynthesisProcess/Hands-on-7.md","lastUpdated":1704173350000}'),d={name:"AmmoniaSynthesisProcess/Hands-on-7.md"},a=n('<h1 id="特定单元-合成氨反应系统" tabindex="-1">特定单元-合成氨反应系统 <a class="header-anchor" href="#特定单元-合成氨反应系统" aria-label="Permalink to &quot;特定单元-合成氨反应系统&quot;">​</a></h1><h2 id="工艺帮助" tabindex="-1">工艺帮助 <a class="header-anchor" href="#工艺帮助" aria-label="Permalink to &quot;工艺帮助&quot;">​</a></h2><h3 id="流程简介" tabindex="-1">流程简介 <a class="header-anchor" href="#流程简介" aria-label="Permalink to &quot;流程简介&quot;">​</a></h3><ul><li>从边界来的新鲜工艺气经原料气缓冲罐 D101 分液后，进入合成气压缩机 K101 低压段，压缩至 5.9MPa，出低压段后先经水冷器 E101，然后经氨冷器 E102 冷却至 8°C，进入段间分离罐 D102 进行气液分离，D102 出来的气体进合成压缩机 K101 高压缸。</li><li>合成回路来的循环气与经高压段压缩后的氢氨气混合进入压缩机循环段进一步压缩至 14.6MPa。</li><li>压缩机出口气体分成两路: 一路经水冷器冷却后一股经防喘振阀 FIC1002 返回至压缩机高压段，另一股经防喘振阀 FIC1003 返回至压缩机循环段;另一路经换热器 E104 加热后进合成塔 T101 进行合成反应。</li><li>合成气经锅炉水换热器 E105/E106 冷却至 167°C 后，再经换热器 E104、水冷器 E107 冷却至 37°C 后分成两路: 一路经换热器 E110 换热，一路经氨冷器 E108 冷却，然后再进行混合，混合后又经氨冷器 E109 冷却至-25°C 进入产品罐 D103。</li><li>液氨与未反应的合成气在产品罐 D103 中分离，液氨经液控阀 LIC1003 控制出装置，未反应的合成气与 E110 换热后分成两路: 一路经 PIC1001 去下游装置 (驰放气罐)一路返回至压缩机与高压段压缩后的氢氨气混合进入压缩机循环段，重复上述循环。</li></ul><h3 id="设备列表" tabindex="-1">设备列表 <a class="header-anchor" href="#设备列表" aria-label="Permalink to &quot;设备列表&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">D101</td><td style="text-align:center;">原料气分离罐</td><td style="text-align:center;">09</td><td style="text-align:center;">E106</td><td style="text-align:center;">锅炉给水二级换热器</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">D102</td><td style="text-align:center;">合成气压缩机段间分离罐</td><td style="text-align:center;">10</td><td style="text-align:center;">E107</td><td style="text-align:center;">合成气水冷器</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">D103</td><td style="text-align:center;">产品罐</td><td style="text-align:center;">11</td><td style="text-align:center;">E108</td><td style="text-align:center;">合成气一级氨冷器</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">E101</td><td style="text-align:center;">段间水冷器</td><td style="text-align:center;">12</td><td style="text-align:center;">E109</td><td style="text-align:center;">合成气二级氢冷器</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">E102</td><td style="text-align:center;">段间氨冷器</td><td style="text-align:center;">13</td><td style="text-align:center;">E110</td><td style="text-align:center;">合成气换热器</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">E103</td><td style="text-align:center;">防喘震水冷器</td><td style="text-align:center;">14</td><td style="text-align:center;">F101</td><td style="text-align:center;">开工加热炉</td></tr><tr><td style="text-align:center;">07</td><td style="text-align:center;">E104</td><td style="text-align:center;">合成塔进出气换热器</td><td style="text-align:center;">15</td><td style="text-align:center;">K101</td><td style="text-align:center;">合成气压缩机</td></tr><tr><td style="text-align:center;">08</td><td style="text-align:center;">E105</td><td style="text-align:center;">锅炉给水一级换热器</td><td style="text-align:center;">16</td><td style="text-align:center;">T101</td><td style="text-align:center;">合成反应器</td></tr></tbody></table><h3 id="仪表列表" tabindex="-1">仪表列表 <a class="header-anchor" href="#仪表列表" aria-label="Permalink to &quot;仪表列表&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">单位</th><th style="text-align:center;">正常值</th><th style="text-align:center;">控制范围</th><th style="text-align:center;">描述</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">FIC1001</td><td style="text-align:center;">m<sup>3</sup>/H</td><td style="text-align:center;">182221</td><td style="text-align:center;">170000~200000</td><td style="text-align:center;">K101 一级防喘振流量控制</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">FIC1002</td><td style="text-align:center;">m<sup>3</sup>/H</td><td style="text-align:center;">182221</td><td style="text-align:center;">170000~200000</td><td style="text-align:center;">K101 二级防喘振流量控制</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">FIC1011</td><td style="text-align:center;">km<sup>3</sup>/H</td><td style="text-align:center;">698</td><td style="text-align:center;">650~710</td><td style="text-align:center;">K101 二级出口流量控制</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">LIC1001</td><td style="text-align:center;">%</td><td style="text-align:center;">20</td><td style="text-align:center;">10~30</td><td style="text-align:center;">D101 液位</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">LIC1002</td><td style="text-align:center;">%</td><td style="text-align:center;">20</td><td style="text-align:center;">10~30</td><td style="text-align:center;">D102 液位</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">LIC1003</td><td style="text-align:center;">%</td><td style="text-align:center;">50</td><td style="text-align:center;">40~60</td><td style="text-align:center;">D103 液位</td></tr><tr><td style="text-align:center;">07</td><td style="text-align:center;">PIC1001</td><td style="text-align:center;">MPa</td><td style="text-align:center;">2.3</td><td style="text-align:center;">2.0~2.6</td><td style="text-align:center;">D101 压力</td></tr><tr><td style="text-align:center;">08</td><td style="text-align:center;">PI1002</td><td style="text-align:center;">MPa</td><td style="text-align:center;">5.9</td><td style="text-align:center;">5.4~6.4</td><td style="text-align:center;">K101 高压缸入口压力</td></tr><tr><td style="text-align:center;">09</td><td style="text-align:center;">PI1003</td><td style="text-align:center;">MPa</td><td style="text-align:center;">14.6</td><td style="text-align:center;">14.1~ 15.1</td><td style="text-align:center;">K101 高压缸出口压力</td></tr><tr><td style="text-align:center;">10</td><td style="text-align:center;">PIC1004</td><td style="text-align:center;">MPa</td><td style="text-align:center;">14</td><td style="text-align:center;">13.5~14.5</td><td style="text-align:center;">D103 压力</td></tr><tr><td style="text-align:center;">11</td><td style="text-align:center;">TIC1001</td><td style="text-align:center;">°C</td><td style="text-align:center;">140</td><td style="text-align:center;">135~145</td><td style="text-align:center;">合成塔入口温度</td></tr><tr><td style="text-align:center;">12</td><td style="text-align:center;">TI1004</td><td style="text-align:center;">°C</td><td style="text-align:center;">63</td><td style="text-align:center;">50~70</td><td style="text-align:center;">K101 高压缸出口温度</td></tr><tr><td style="text-align:center;">13</td><td style="text-align:center;">TIC1026</td><td style="text-align:center;">°C</td><td style="text-align:center;">490</td><td style="text-align:center;">480 ~ 500</td><td style="text-align:center;">合成塔 T101 上部温度</td></tr><tr><td style="text-align:center;">14</td><td style="text-align:center;">TIC1028</td><td style="text-align:center;">°C</td><td style="text-align:center;">485</td><td style="text-align:center;">475~495</td><td style="text-align:center;">合成塔 T101 中部温度</td></tr><tr><td style="text-align:center;">15</td><td style="text-align:center;">TIC1030</td><td style="text-align:center;">°C</td><td style="text-align:center;">450</td><td style="text-align:center;">440~460</td><td style="text-align:center;">合成塔 T101 下部温度</td></tr></tbody></table><h2 id="科目三-作业现场隐患排查" tabindex="-1">科目三-作业现场隐患排查 <a class="header-anchor" href="#科目三-作业现场隐患排查" aria-label="Permalink to &quot;科目三-作业现场隐患排查&quot;">​</a></h2><h3 id="原料中断" tabindex="-1">原料中断 <a class="header-anchor" href="#原料中断" aria-label="Permalink to &quot;原料中断&quot;">​</a></h3><h4 id="在-dcs-画面中" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><ol><li>在<code>辅操台</code>画面中，点击<strong>紧急停车</strong> 按钮 <img src="http://qiniu.yeshan-taoist.cn/20231219133958.png" alt="20231219133958"></li><li>在<code>合成气压缩机</code>画面中 <ol><li>全开<code>D101压力控制PIC1001</code>、<code>K101一级防喘振FIC1001</code>、<code>K101二级防喘振FIC1002</code>、<code>K101出口工艺气FIC1003</code></li><li>关闭<code>XV1001</code>和<code>XV1006</code><img src="http://qiniu.yeshan-taoist.cn/20231219134852.png" alt="20231219134852"></li></ol></li><li>在<code>合成塔</code>画面中，关闭 <code>D103液位LIC1003</code>、<code>XV1003</code>、<code>XV1004</code><img src="http://qiniu.yeshan-taoist.cn/20231219135225.png" alt="20231219135225"></li><li>在<code>合成塔温度控制</code>画面中 <ol><li><code>T101一段床层温度控制TIC1025</code>、<code>T101二段床层温度控制TIC1027</code> 和 <code>T101三段床层温度控制TIC1029</code> 置 <code>MAN(手动)</code></li><li>关闭 <code>MIC1002</code>、<code>MIC1003</code>、<code>MIC1004</code>、<code>MIC1001</code> 和 <code>MIC1005</code><img src="http://qiniu.yeshan-taoist.cn/20231219140544.png" alt="20231219140544"></li></ol></li></ol><h4 id="在现场" tabindex="-1">在现场 <a class="header-anchor" href="#在现场" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li><strong>关闭</strong><code>原料进 D101 现场阀 VX1D101</code><img src="http://qiniu.yeshan-taoist.cn/20231219140934.png" alt="20231219140934"></li><li><strong>关闭</strong><code>D103产品罐LV1003液位调节阀前后手阀</code><img src="http://qiniu.yeshan-taoist.cn/20231219141306.png" alt="20231219141306"></li><li><strong>全开</strong> <code>E109制冷剂进现场阀VX1E109</code>、<code>E108制冷剂进现场阀VX1E108</code><img src="http://qiniu.yeshan-taoist.cn/20231219141756.png" alt="20231219141756"></li><li><strong>全开</strong> <code>E102制冷剂进现场阀VX1E102</code>(在二层) <img src="http://qiniu.yeshan-taoist.cn/20231219142116.png" alt="20231219142116"></li><li><strong>关闭</strong> <code>D103合成气返回现场阀VX1E110</code>(在二层 E110 左侧) <img src="http://qiniu.yeshan-taoist.cn/20231219142225.png" alt="20231219142225"></li></ol><h3 id="冷却水中断" tabindex="-1">冷却水中断 <a class="header-anchor" href="#冷却水中断" aria-label="Permalink to &quot;冷却水中断&quot;">​</a></h3><blockquote><p>同<code>原料中断</code></p></blockquote><h3 id="原料气分离器高液位联锁" tabindex="-1">原料气分离器高液位联锁 <a class="header-anchor" href="#原料气分离器高液位联锁" aria-label="Permalink to &quot;原料气分离器高液位联锁&quot;">​</a></h3><blockquote><p>同<code>原料中断</code></p></blockquote><h2 id="科目四-作业现场应急处置" tabindex="-1">科目四-作业现场应急处置 <a class="header-anchor" href="#科目四-作业现场应急处置" aria-label="Permalink to &quot;科目四-作业现场应急处置&quot;">​</a></h2>',19),r=[a];function i(c,s,g,o,y,x){return e(),l("div",null,r)}const C=t(d,[["render",i]]);export{u as __pageData,C as default};
