import{_ as l,o as a,c as i,V as t}from"./chunks/framework.55251076.js";const b=JSON.parse('{"title":"通用单元-精馏塔","description":"","frontmatter":{},"headers":[],"relativePath":"Hands-on-Sources/DistillationColumn.md","filePath":"Hands-on-Sources/DistillationColumn.md","lastUpdated":1707025275000}'),o={name:"Hands-on-Sources/DistillationColumn.md"},e=t('<h1 id="通用单元-精馏塔" tabindex="-1">通用单元-精馏塔 <a class="header-anchor" href="#通用单元-精馏塔" aria-label="Permalink to &quot;通用单元-精馏塔&quot;">​</a></h1><h2 id="工艺帮助" tabindex="-1">工艺帮助 <a class="header-anchor" href="#工艺帮助" aria-label="Permalink to &quot;工艺帮助&quot;">​</a></h2><h3 id="流程简介" tabindex="-1">流程简介 <a class="header-anchor" href="#流程简介" aria-label="Permalink to &quot;流程简介&quot;">​</a></h3><ul><li>本单元是一种加压精馏操作</li><li>73°C 的原料液经流量 FIC1001 控制流量后，从精馏塔 T101 的第 16 块塔板进料</li><li>塔顶蒸气经塔顶全凝器冷凝为液体以后进入回流罐 D101;回流罐 D101 的液体由泵 P101A/B 抽出，一部分作为回流液由调节器 FIC1004 控流量控制送回 T101 塔顶</li><li>另一部分则作为产品，其流量由调节器 FIC1003 控制。回流罐的液位由调节器 LIC1003 和 FIC1003 构成的串级控制回路控制</li><li>T101 操作压力由调节器 PIC1002 分程控制为 0.41MpaG，同时调节器 PIC1001 将调节回流罐的气相出料，保证系统的安全和稳定</li><li>塔釜液体的一部分经再沸器 E102A/B 回精馏塔，另一部分由调节器 FIC1002 控制流量，作为塔低采出产品</li><li>调节器 LIC1001 和 FIC1002 构成串级控制回路，调节精馏塔的液位</li><li>再沸器用低压蒸汽加热，加热蒸汽流量由调节器 TIC1001 控制，其冷凝液送 D102。D102 的液位由调节器 LIC1002 调节</li></ul><h3 id="设备列表" tabindex="-1">设备列表 <a class="header-anchor" href="#设备列表" aria-label="Permalink to &quot;设备列表&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">T101</td><td style="text-align:center;">精馏塔</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">E101</td><td style="text-align:center;">精馏塔塔顶冷凝器</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">D101</td><td style="text-align:center;">精馏塔塔顶回流罐</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">P101A/B</td><td style="text-align:center;">回流液输送泵</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">E102</td><td style="text-align:center;">精馏塔塔釜再沸器</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">D102</td><td style="text-align:center;">精馏塔塔釜蒸汽缓冲罐</td></tr></tbody></table><h3 id="仪表列表" tabindex="-1">仪表列表 <a class="header-anchor" href="#仪表列表" aria-label="Permalink to &quot;仪表列表&quot;">​</a></h3><h3 id="流程简图" tabindex="-1">流程简图 <a class="header-anchor" href="#流程简图" aria-label="Permalink to &quot;流程简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609133601.png" alt="20230609133601"></p><h3 id="现场简图" tabindex="-1">现场简图 <a class="header-anchor" href="#现场简图" aria-label="Permalink to &quot;现场简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609133707.png" alt="20230609133707"></p><h2 id="科目三-作业现场隐患排查" tabindex="-1">科目三-作业现场隐患排查 <a class="header-anchor" href="#科目三-作业现场隐患排查" aria-label="Permalink to &quot;科目三-作业现场隐患排查&quot;">​</a></h2><h3 id="冷却水供应不足" tabindex="-1">冷却水供应不足 <a class="header-anchor" href="#冷却水供应不足" aria-label="Permalink to &quot;冷却水供应不足&quot;">​</a></h3><h4 id="在-dcs-画面中" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><blockquote><p><img src="http://qiniu.yeshan-taoist.cn/20230610221834.png" alt="20230610221834"></p></blockquote><ol><li>关闭<strong>塔釜温度控制 TIC1001</strong></li><li>关闭<strong>T101 进料控制 FIC1001</strong></li><li>关闭<strong>塔釜出料流量控制 FIC1002</strong></li></ol><h4 id="在现场" tabindex="-1">在现场 <a class="header-anchor" href="#在现场" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><blockquote><p><img src="http://qiniu.yeshan-taoist.cn/20230610222436.png" alt="20230610222436"></p></blockquote><ol><li>打开 <strong>T101 不合格线阀 VX2T101</strong></li><li>打开 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong></li><li>关闭 <strong>D101 合格线阀 VX1D101</strong></li></ol><h4 id="工艺指标控制" tabindex="-1">工艺指标控制 <a class="header-anchor" href="#工艺指标控制" aria-label="Permalink to &quot;工艺指标控制&quot;">​</a></h4><blockquote><p><img src="http://qiniu.yeshan-taoist.cn/20230610223221.png" alt="20230610223221"></p></blockquote><ol><li><strong>回流罐压力控制 PIC1001</strong>，将其控制在 0.41Mpa 左右</li></ol><h4 id="上述步骤操作快速完成后可以提交" tabindex="-1">上述步骤操作快速完成后可以提交 <a class="header-anchor" href="#上述步骤操作快速完成后可以提交" aria-label="Permalink to &quot;上述步骤操作快速完成后可以提交&quot;">​</a></h4><h3 id="长时间停电" tabindex="-1">长时间停电 <a class="header-anchor" href="#长时间停电" aria-label="Permalink to &quot;长时间停电&quot;">​</a></h3><h4 id="在-dcs-画面中-1" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中-1" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230610221834.png" alt="20230610221834"></p><ol><li>关闭<strong>塔釜温度控制 TIC1001</strong></li><li>关闭<strong>T101 进料控制 FIC1001</strong></li><li>关闭<strong>塔釜出料流量控制 FIC1002</strong></li></ol><h4 id="在现场-1" tabindex="-1">在现场 <a class="header-anchor" href="#在现场-1" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li><strong>打开不合格线阀和关闭合格线阀</strong><ol><li>打开 <strong>T101 不合格线阀 VX2T101</strong></li><li>打开 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong></li><li>关闭 <strong>D101 合格线阀 VX1D101</strong><img src="http://qiniu.yeshan-taoist.cn/20230610222436.png" alt="20230610222436"></li></ol></li><li>关闭<strong>回流液输送泵 P101A 泵出口阀</strong><img src="http://qiniu.yeshan-taoist.cn/20230610224107.png" alt="20230610224107"></li></ol><h4 id="工艺指标控制-1" tabindex="-1">工艺指标控制 <a class="header-anchor" href="#工艺指标控制-1" aria-label="Permalink to &quot;工艺指标控制&quot;">​</a></h4><blockquote><p><img src="http://qiniu.yeshan-taoist.cn/20230610223221.png" alt="20230610223221"></p></blockquote><ol><li><strong>回流罐压力控制 PIC1001</strong>，将其控制在 0.41Mpa 左右</li></ol><h4 id="上述步骤操作快速完成后可以提交-1" tabindex="-1">上述步骤操作快速完成后可以提交 <a class="header-anchor" href="#上述步骤操作快速完成后可以提交-1" aria-label="Permalink to &quot;上述步骤操作快速完成后可以提交&quot;">​</a></h4><h3 id="原料中断事故" tabindex="-1">原料中断事故 <a class="header-anchor" href="#原料中断事故" aria-label="Permalink to &quot;原料中断事故&quot;">​</a></h3><h4 id="在-dcs-画面中-2" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中-2" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><blockquote><p><img src="http://qiniu.yeshan-taoist.cn/20230610225507.png" alt="20230610225507"></p></blockquote><ol><li><strong>塔釜温度控制 TIC1001</strong>切换为手动，OP 值设置为 40</li><li><strong>塔顶采出流量控制 FIC1003</strong>切换为手动，OP 值设置为 0</li><li>关闭<strong>塔釜出料流量控制 FIC1002</strong></li></ol><h4 id="在现场-2" tabindex="-1">在现场 <a class="header-anchor" href="#在现场-2" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li>位置如图所示</li><li><img src="http://qiniu.yeshan-taoist.cn/20230610222436.png" alt="20230610222436"></li><li>打开 <strong>T101 不合格线阀 VX2T101</strong></li><li>打开 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong></li><li>关闭 <strong>D101 合格线阀 VX1D101</strong></li></ol><h4 id="在-dcs-画面中-3" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中-3" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><ol><li><strong>塔釜温度控制 TIC1001</strong>OP 值设置为 30</li></ol><h4 id="上述步骤操作快速完成后可以提交-2" tabindex="-1">上述步骤操作快速完成后可以提交 <a class="header-anchor" href="#上述步骤操作快速完成后可以提交-2" aria-label="Permalink to &quot;上述步骤操作快速完成后可以提交&quot;">​</a></h4><h3 id="加热蒸汽中断" tabindex="-1">加热蒸汽中断 <a class="header-anchor" href="#加热蒸汽中断" aria-label="Permalink to &quot;加热蒸汽中断&quot;">​</a></h3><h4 id="在-dcs-画面中-4" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中-4" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><blockquote><p><img src="http://qiniu.yeshan-taoist.cn/20230610230927.png" alt="20230610230927"></p></blockquote><ol><li>关闭 <strong>T101 进料流量控制 FIC1001</strong></li><li>关闭<strong>塔顶采出流量控制 FIC1003</strong></li><li>关闭<strong>塔釜出料流量控制 FIC1002</strong></li><li>关闭<strong>塔顶回流流量控制 FIC1004</strong></li></ol><h4 id="在现场-3" tabindex="-1">在现场 <a class="header-anchor" href="#在现场-3" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li><p><strong>打开不合格线阀和关闭合格线阀</strong><img src="http://qiniu.yeshan-taoist.cn/20230610222436.png" alt="20230610222436"></p><ol><li>打开 <strong>T101 不合格线阀 VX2T101</strong></li><li>打开 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong></li><li>关闭 <strong>D101 合格线阀 VX1D101</strong></li></ol></li><li><p>关闭<strong>回流液输送泵 P101A 泵出口阀，停泵</strong><img src="http://qiniu.yeshan-taoist.cn/20230610224107.png" alt="20230610224107"></p></li></ol><h4 id="上述步骤操作快速完成后可以提交-3" tabindex="-1">上述步骤操作快速完成后可以提交 <a class="header-anchor" href="#上述步骤操作快速完成后可以提交-3" aria-label="Permalink to &quot;上述步骤操作快速完成后可以提交&quot;">​</a></h4><h3 id="回流中断" tabindex="-1">回流中断 <a class="header-anchor" href="#回流中断" aria-label="Permalink to &quot;回流中断&quot;">​</a></h3><blockquote><p>快速操作</p></blockquote><ol><li>在现场，启动回流液输送泵 P101B</li><li>在 DCS 画面中，等待 P101A 泵停止后立即回到现场打开 P101B 的出口阀</li><li>提交即可得分</li></ol><h2 id="科目四-作业现场应急处置" tabindex="-1">科目四-作业现场应急处置 <a class="header-anchor" href="#科目四-作业现场应急处置" aria-label="Permalink to &quot;科目四-作业现场应急处置&quot;">​</a></h2><h3 id="回流罐切水阀法兰泄漏着火" tabindex="-1">回流罐切水阀法兰泄漏着火 <a class="header-anchor" href="#回流罐切水阀法兰泄漏着火" aria-label="Permalink to &quot;回流罐切水阀法兰泄漏着火&quot;">​</a></h3><h4 id="外操员" tabindex="-1">外操员 <a class="header-anchor" href="#外操员" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>正在巡检，发现回流罐 D101 切水法兰处泄漏着火</li><li>通过对讲机向班长报告：回流罐 D101 切水法兰处泄漏着火</li><li>迅速在附近拿到灭火器站到火焰的上风口对准火焰根部灭火</li><li>灭火失败，报告班长：尝试灭火，但火没有灭掉</li><li>回中控室携带 F 型扳手和正压式空气呼吸器</li><li>回到现场，等待下一步操作指令</li></ol><h4 id="班长" tabindex="-1">班长 <a class="header-anchor" href="#班长" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>接到外操员的报告后，通过广播启动应急预案：启动《车间泄漏着火应急预案》</li><li>通过中控室电话向调度报告：回流罐 D101 切水法兰处泄漏着火，已启动应急预案</li><li>通过对讲机命令安全员：请组织人员到 1 号门拉警戒线</li><li>携带 F 型扳手和正压式空气呼吸器</li><li>通过对讲机命令外操员：启动消防炮</li><li>班长命令主操拨打 119</li></ol><h4 id="外操员-1" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-1" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>打开消防炮</li></ol><h4 id="主操" tabindex="-1">主操 <a class="header-anchor" href="#主操" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li>通过中控室电话向 119 报警：精馏车间回流罐 D101 切水阀处泄漏着火，火势较大，请派消防车来，报警人张三</li></ol><h4 id="班长-1" tabindex="-1">班长 <a class="header-anchor" href="#班长-1" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>命令安全员：请组织人员到 1 号门引导消防车</li><li>命令主操和外操员：执行紧急停车操作</li></ol><h4 id="外操员在现场执行紧急停车操作" tabindex="-1">外操员在现场执行紧急停车操作 <a class="header-anchor" href="#外操员在现场执行紧急停车操作" aria-label="Permalink to &quot;外操员在现场执行紧急停车操作&quot;">​</a></h4><ol><li>打开 <strong>T101 不合格线阀 VX2T101</strong> 和 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong> 和 <strong>D101 合格线阀 VX1D101</strong></li></ol><h4 id="主操在中控执行紧急停车操作" tabindex="-1">主操在中控执行紧急停车操作 <a class="header-anchor" href="#主操在中控执行紧急停车操作" aria-label="Permalink to &quot;主操在中控执行紧急停车操作&quot;">​</a></h4><blockquote><p>将如图所示位置画面中调节阀做以下操作 <img src="http://qiniu.yeshan-taoist.cn/20230618133106.png" alt="20230618133106"></p></blockquote><ol><li>关闭 <strong>T101 进料流量控制阀 FIC1001</strong></li><li>将<strong>塔釜温度控制 TIC1001 切换到手动 MAN，OP 值设置为 50，稍候几秒设置为 0</strong></li><li><strong>加大塔顶回流流量控制阀 FIC1004</strong></li><li><strong>加大塔顶采出流量控制阀 FIC1003</strong></li><li><strong>加大塔釜出料流量控制阀 FIC1002</strong></li><li><strong>全开回流罐压力调节阀 PIC1001 使回流罐 D101 的压力降到 0.05Mpa</strong></li><li>向班长报告：室内操作完毕</li></ol><h4 id="外操员在现场执行紧急停车操作-1" tabindex="-1">外操员在现场执行紧急停车操作 <a class="header-anchor" href="#外操员在现场执行紧急停车操作-1" aria-label="Permalink to &quot;外操员在现场执行紧急停车操作&quot;">​</a></h4><ol><li>关闭回流泵 P101A 出口阀</li><li>回流罐倒空后，停 P101A 泵</li><li>向班长报告：现场操作完毕</li></ol><h4 id="班长-2" tabindex="-1">班长 <a class="header-anchor" href="#班长-2" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>电话调度：事故处理完毕，请派维修人员消漏</li><li>通过广播：解除事故应急预案</li></ol><h3 id="回流泵机械密封泄漏着火-火未扑灭" tabindex="-1">回流泵机械密封泄漏着火-火未扑灭 <a class="header-anchor" href="#回流泵机械密封泄漏着火-火未扑灭" aria-label="Permalink to &quot;回流泵机械密封泄漏着火-火未扑灭&quot;">​</a></h3><h4 id="外操员-2" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-2" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>正在巡检，发现回流泵 P101A 机械密封处泄漏着火</li><li>通过对讲机向班长报告：回流泵 P101A 机械密封处泄漏着火</li><li>迅速在附近拿到灭火器站到火焰的上风口对准火焰根部灭火</li><li>灭火失败，报告班长：尝试灭火，但火没有灭掉</li><li>回中控室携带 F 型扳手和正压式空气呼吸器</li><li>回到现场，等待下一步操作指令</li></ol><h4 id="班长-3" tabindex="-1">班长 <a class="header-anchor" href="#班长-3" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>接到外操员的报告后，通过广播启动应急预案：启动《车间泄漏着火应急预案》</li><li>通过中控室电话向调度报告：回流泵 P101A 机械密封处泄漏着火，已启动应急预案</li><li>通过对讲机命令安全员：请组织人员到 1 号门拉警戒线</li><li>携带 F 型扳手和正压式空气呼吸器</li><li>通过对讲机命令外操员：启动消防炮</li><li>班长命令主操拨打 119</li></ol><h4 id="外操员-3" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-3" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>打开消防炮</li></ol><h4 id="主操-1" tabindex="-1">主操 <a class="header-anchor" href="#主操-1" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li>通过中控室电话向 119 报警：精馏车间 P101 机械密封处泄漏着火，火势较大，请派消防车来，报警人张三</li></ol><h4 id="班长-4" tabindex="-1">班长 <a class="header-anchor" href="#班长-4" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>命令安全员：请组织人员到 1 号门引导消防车</li><li>命令主操和外操员：执行紧急停车操作</li></ol><h4 id="外操员在现场执行紧急停车操作-2" tabindex="-1">外操员在现场执行紧急停车操作 <a class="header-anchor" href="#外操员在现场执行紧急停车操作-2" aria-label="Permalink to &quot;外操员在现场执行紧急停车操作&quot;">​</a></h4><ol><li>打开 <strong>T101 不合格线阀 VX2T101</strong> 和 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong> 和 <strong>D101 合格线阀 VX1D101</strong></li><li>关闭<strong>回流泵 P101A 电源开关</strong></li><li>关闭<strong>回流泵 P101A 的出口阀</strong></li><li>关闭<strong>回流罐底部的回流泵进口总阀</strong></li><li>分别关闭 <strong>FV1002、FV1003、FV1004 的后手阀</strong></li><li>向班长报告：现场操作完毕</li></ol><h4 id="主操在中控执行紧急停车操作-1" tabindex="-1">主操在中控执行紧急停车操作 <a class="header-anchor" href="#主操在中控执行紧急停车操作-1" aria-label="Permalink to &quot;主操在中控执行紧急停车操作&quot;">​</a></h4><ol><li>关闭 <strong>T101 进料流量控制阀 FIC1001</strong></li><li>将<strong>塔釜温度控制 TIC1001 切换到手动 MAN，OP 值设置为 50，稍候几秒设置为 0</strong></li><li><strong>关闭塔顶回流流量控制阀 FIC1004</strong></li><li><strong>关闭塔顶采出流量控制阀 FIC1003</strong></li><li><strong>关闭塔釜出料流量控制阀 FIC1002</strong></li><li>向班长报告：室内操作完毕</li></ol><h4 id="班长-5" tabindex="-1">班长 <a class="header-anchor" href="#班长-5" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>电话调度：事故处理完毕，请派维修人员消漏</li><li>通过广播：解除事故应急预案</li></ol><h3 id="回流泵机械密封泄漏着火-火扑灭" tabindex="-1">回流泵机械密封泄漏着火-火扑灭 <a class="header-anchor" href="#回流泵机械密封泄漏着火-火扑灭" aria-label="Permalink to &quot;回流泵机械密封泄漏着火-火扑灭&quot;">​</a></h3><h4 id="外操员-4" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-4" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>正在巡检，发现回流泵 P101A 机械密封处泄漏着火</li><li>通过对讲机向班长报告：回流泵 P101A 机械密封处泄漏着火</li></ol><h4 id="班长-6" tabindex="-1">班长 <a class="header-anchor" href="#班长-6" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>接到外操员的报告后，通过广播启动应急预案：启动《车间泄漏着火应急预案》</li><li>通过中控室电话向调度报告：回流泵 P101A 机械密封处泄漏着火，已启动应急预案</li><li>通过对讲机命令安全员：请组织人员到 1 号门拉警戒线</li><li>携带 F 型扳手和正压式空气呼吸器</li></ol><h4 id="外操员-5" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-5" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>回中控室，携带 F 型扳手和正压式空气呼吸器</li><li>回到事故现场</li><li>迅速在附近拿到灭火器站到火焰的上风口对准火焰根部灭火</li><li>火已扑灭，向班长报告：火已扑灭</li></ol><h4 id="班长-7" tabindex="-1">班长 <a class="header-anchor" href="#班长-7" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>命令主操：请监视装置生产情况</li><li>命令外操员：启动备用泵 P101B</li></ol><h4 id="外操员-6" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-6" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>启用备用泵 P101B，打开其出口阀</li><li>关闭事故泵 P101A 电源，关闭其出入口阀</li><li>打开泵体去火炬阀 VX1P101A</li><li>打开入口导淋阀 VX2P101A</li><li>无液体排出后，关闭前两个步骤打开的阀门</li><li>向班长报告：现场操作完毕</li></ol><h4 id="班长-8" tabindex="-1">班长 <a class="header-anchor" href="#班长-8" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>电话调度：事故处理完毕，请派维修人员消漏</li><li>通过广播：解除事故应急预案</li></ol><h3 id="精馏塔塔釜出料阀法兰泄漏着火" tabindex="-1">精馏塔塔釜出料阀法兰泄漏着火 <a class="header-anchor" href="#精馏塔塔釜出料阀法兰泄漏着火" aria-label="Permalink to &quot;精馏塔塔釜出料阀法兰泄漏着火&quot;">​</a></h3><h4 id="外操员-7" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-7" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>正在巡检，发现精馏塔 T101 塔釜出料阀法兰泄漏着火</li><li>通过对讲机向班长报告：精馏塔 T101 塔釜出料阀法兰泄漏着火</li><li>迅速在附近拿到灭火器站到火焰的上风口对准火焰根部灭火</li><li>灭火失败，报告班长：尝试灭火，但火没有灭掉</li><li>回中控室携带 F 型扳手和正压式空气呼吸器</li><li>回到现场，等待下一步操作指令</li></ol><h4 id="班长-9" tabindex="-1">班长 <a class="header-anchor" href="#班长-9" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>接到外操员的报告后，通过广播启动应急预案：启动《车间泄漏着火应急预案》</li><li>通过中控室电话向调度报告：精馏塔 T101 塔釜出料阀法兰泄漏着火，已启动应急预案</li><li>通过对讲机命令安全员：请组织人员到 1 号门拉警戒线</li><li>携带 F 型扳手和正压式空气呼吸器</li><li>通过对讲机命令外操员：启动消防炮</li><li>班长命令主操拨打 119</li></ol><h4 id="外操员-8" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-8" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>打开消防炮</li></ol><h4 id="主操-2" tabindex="-1">主操 <a class="header-anchor" href="#主操-2" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li>通过中控室电话向 119 报警：精馏塔 T101 塔釜出料阀法兰处泄漏着火，火势较大，请派消防车来，报警人张三</li></ol><h4 id="班长-10" tabindex="-1">班长 <a class="header-anchor" href="#班长-10" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>命令安全员：请组织人员到 1 号门引导消防车</li><li>命令主操和外操员：执行紧急停车操作</li></ol><h4 id="外操员在现场执行紧急停车操作-3" tabindex="-1">外操员在现场执行紧急停车操作 <a class="header-anchor" href="#外操员在现场执行紧急停车操作-3" aria-label="Permalink to &quot;外操员在现场执行紧急停车操作&quot;">​</a></h4><ol><li>打开 <strong>T101 不合格线阀 VX2T101</strong> 和 <strong>D101 不合格线阀 VX2D101</strong></li><li>关闭 <strong>T101 合格线阀 VX1T101</strong> 和 <strong>D101 合格线阀 VX1D101</strong></li></ol><h4 id="主操在中控执行紧急停车操作-2" tabindex="-1">主操在中控执行紧急停车操作 <a class="header-anchor" href="#主操在中控执行紧急停车操作-2" aria-label="Permalink to &quot;主操在中控执行紧急停车操作&quot;">​</a></h4><ol><li>关闭 <strong>T101 进料流量控制阀 FIC1001</strong></li><li>将<strong>塔釜温度控制 TIC1001 切换到手动 MAN，OP 值设置为 50，稍候几秒设置为 0</strong></li><li><strong>加大塔釜出料流量控制阀 FIC1002</strong></li><li><strong>全开回流罐压力调节阀 PIC1001 使回流罐 D101 的压力降到 0.05Mpa</strong></li></ol><h4 id="外操员在现场执行紧急停车操作-4" tabindex="-1">外操员在现场执行紧急停车操作 <a class="header-anchor" href="#外操员在现场执行紧急停车操作-4" aria-label="Permalink to &quot;外操员在现场执行紧急停车操作&quot;">​</a></h4><ol><li>关闭回流泵 P101A 出口阀</li><li>保证塔顶不超温，停 P101A 泵</li></ol><h4 id="主操在中控执行紧急停车操作-3" tabindex="-1">主操在中控执行紧急停车操作 <a class="header-anchor" href="#主操在中控执行紧急停车操作-3" aria-label="Permalink to &quot;主操在中控执行紧急停车操作&quot;">​</a></h4><ol><li><strong>关闭塔顶采出流量控制阀 FIC1003</strong></li><li>塔釜液位排空后，<strong>关闭塔釜出料流量控制阀 FIC1002</strong></li><li><strong>关闭塔顶回流流量控制阀 FIC1004</strong></li><li>向班长报告：室内操作完毕</li></ol><h4 id="外操员在现场执行紧急停车操作-5" tabindex="-1">外操员在现场执行紧急停车操作 <a class="header-anchor" href="#外操员在现场执行紧急停车操作-5" aria-label="Permalink to &quot;外操员在现场执行紧急停车操作&quot;">​</a></h4><ol><li>关闭 FV1003 后手阀</li><li>关闭 FV1002 后手阀</li><li>关闭 FV1004 后手阀</li><li>关闭 PV1002 前后手阀</li><li>向班长报告：现场操作完毕</li></ol><h4 id="班长-11" tabindex="-1">班长 <a class="header-anchor" href="#班长-11" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li>电话调度：事故处理完毕，请派维修人员消漏</li><li>通过广播：解除事故应急预案</li></ol>',126),r=[e];function n(s,h,d,c,g,u){return a(),i("div",null,r)}const P=l(o,[["render",n]]);export{b as __pageData,P as default};
