import{_ as t,o as l,c as a,V as o}from"./chunks/framework.55251076.js";const b=JSON.parse('{"title":"特定单元-固定床反应器","description":"","frontmatter":{},"headers":[],"relativePath":"Hands-on-Sources/FxedReactionBed.md","filePath":"Hands-on-Sources/FxedReactionBed.md","lastUpdated":1707094448000}'),i={name:"Hands-on-Sources/FxedReactionBed.md"},e=o('<h1 id="特定单元-固定床反应器" tabindex="-1">特定单元-固定床反应器 <a class="header-anchor" href="#特定单元-固定床反应器" aria-label="Permalink to &quot;特定单元-固定床反应器&quot;">​</a></h1><h2 id="工艺帮助" tabindex="-1">工艺帮助 <a class="header-anchor" href="#工艺帮助" aria-label="Permalink to &quot;工艺帮助&quot;">​</a></h2><h3 id="流程简介" tabindex="-1">流程简介 <a class="header-anchor" href="#流程简介" aria-label="Permalink to &quot;流程简介&quot;">​</a></h3><ul><li>本单元模拟的乙炔加氢反应系统，其作用是除去脱乙烷塔顶气相混合碳二组分中的乙炔。</li><li>脱乙烷塔塔顶物料经加热后进入乙炔转化器 (R101A/B)采用选择性加氢生成乙烯的方法，除去物料中所含的乙炔。本装置设有两台反应器，进行切换操作，催化剂在不影响连续操作的情况下，用过热蒸汽和空气的混合物对催化剂进行再生</li><li>该加氢步骤分两步完成以提高反应的选择性。反应都是在气相中进行的。通过流量控制将氢气干燥器的高纯度 (95% (mol) ) 气加入到反应器进料中。通过流量控制还加入少量粗氢 (含有 CO) 以调节催化剂的活性。高纯度氢和粗还通过流量控制加入到二段反应器进料中。反应器进料首先通过与乙炔转化器进料/排放物流换热器(E101)中的反应器排放物流换热，然后与乙炔转化器进料预热器 E102 中的低压蒸汽换热被加热到反应温度。然后，它进入第一个反应器床层，并向下流经催化剂床层。在第一个床层中应有大约 75%的乙炔被转化。一段的排放物流在乙炔转化器中间冷却器中被冷却以脱除反应的热量，然后进入第二个反应器床层，对剩余的乙炔进行加氢反应。</li></ul><h3 id="设备列表" tabindex="-1">设备列表 <a class="header-anchor" href="#设备列表" aria-label="Permalink to &quot;设备列表&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">E101</td><td style="text-align:center;">进出料换热器</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">E102</td><td style="text-align:center;">进料加热器</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">E103</td><td style="text-align:center;">R101A/B 中间冷却器</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">E104</td><td style="text-align:center;">R102A/B 中间冷却器</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">E105</td><td style="text-align:center;">出料冷却器</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">R101A/B</td><td style="text-align:center;">乙炔转化器</td></tr><tr><td style="text-align:center;">07</td><td style="text-align:center;">R102A/B</td><td style="text-align:center;">乙炔转化器</td></tr><tr><td style="text-align:center;">08</td><td style="text-align:center;">P102A/B</td><td style="text-align:center;">原料进料泵</td></tr><tr><td style="text-align:center;">09</td><td style="text-align:center;">D101</td><td style="text-align:center;">凝液罐</td></tr></tbody></table><h3 id="仪表列表" tabindex="-1">仪表列表 <a class="header-anchor" href="#仪表列表" aria-label="Permalink to &quot;仪表列表&quot;">​</a></h3><h3 id="流程简图" tabindex="-1">流程简图 <a class="header-anchor" href="#流程简图" aria-label="Permalink to &quot;流程简图&quot;">​</a></h3><h4 id="进料换热系统" tabindex="-1">进料换热系统 <a class="header-anchor" href="#进料换热系统" aria-label="Permalink to &quot;进料换热系统&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230609084835.png" alt="20230609084835"></p><h4 id="反应器部分" tabindex="-1">反应器部分 <a class="header-anchor" href="#反应器部分" aria-label="Permalink to &quot;反应器部分&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230609084929.png" alt="20230609084929"></p><h3 id="现场简图" tabindex="-1">现场简图 <a class="header-anchor" href="#现场简图" aria-label="Permalink to &quot;现场简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609085232.png" alt="20230609085232"></p><h2 id="科目三-作业现场隐患排查" tabindex="-1">科目三-作业现场隐患排查 <a class="header-anchor" href="#科目三-作业现场隐患排查" aria-label="Permalink to &quot;科目三-作业现场隐患排查&quot;">​</a></h2><h3 id="反应器氢气中断" tabindex="-1">反应器氢气中断 <a class="header-anchor" href="#反应器氢气中断" aria-label="Permalink to &quot;反应器氢气中断&quot;">​</a></h3><blockquote><p><strong>反应器泄压和凝液罐泄液完毕提交</strong></p></blockquote><h4 id="在-dcs-画面中" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><ol><li>在<code>进料换热系统</code>画面中，<strong>切断一段反应器氢气进料 FIC1002、切断二段反应器氢气进料 FIC1003、切断烃进料 FIC1001、关闭氢气进料切断阀 UV1002、关闭物料去产品出装置的切断阀 UV1003</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093309.png" alt="20230609093309"></li></ul></li><li>在<code>进料换热系统</code>画面中，打开<strong>凝液罐液位调节阀 LIC1001</strong>，切换到手动，OP 值设置为 100 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609094432.png" alt="20230609094432"></li></ul></li><li>在<code>反应器部分</code>画面中，打开<strong>反应器压力调节阀 PIC1001</strong>，切换到手动，OP 值设置为 100 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609094717.png" alt="20230609094717"></li></ul></li></ol><h4 id="在现场" tabindex="-1">在现场 <a class="header-anchor" href="#在现场" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li>关闭<strong>蒸汽进料前阀 TV1001AI</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609092826.png" alt="20230609092826"></li></ul></li><li>打开产品<strong>不合格油出装置阀门 VI4R101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093108.png" alt="20230609093108"></li></ul></li><li>打开 R101 二楼的<strong>R101A 的排绿阀 VX1R101A</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093521.png" alt="20230609093521"></li></ul></li><li>打开 R101 一楼的<strong>R101B 的排绿阀 VX1R101B</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093742.png" alt="20230609093742"></li></ul></li></ol><h3 id="冷却水中断" tabindex="-1">冷却水中断 <a class="header-anchor" href="#冷却水中断" aria-label="Permalink to &quot;冷却水中断&quot;">​</a></h3><blockquote><p><strong>反应器泄压完毕提交</strong></p></blockquote><h4 id="在-dcs-画面中-1" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中-1" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><ol><li>在<code>进料换热系统</code>画面中，<strong>切断一段反应器氢气进料 FIC1002、切断二段反应器氢气进料 FIC1003、切断烃进料 FIC1001、关闭物料去产品出装置的切断阀 UV1003</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609100302.png" alt="20230609100302"></li></ul></li><li>在<code>反应器部分</code>画面中，打开<strong>反应器压力调节阀 PIC1001</strong>，切换到手动，OP 值设置为 100 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609094717.png" alt="20230609094717"></li></ul></li></ol><h4 id="在现场-1" tabindex="-1">在现场 <a class="header-anchor" href="#在现场-1" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li>打开产品<strong>不合格油出装置阀门 VI4R101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093108.png" alt="20230609093108"></li></ul></li></ol><h3 id="反应器飞温" tabindex="-1">反应器飞温 <a class="header-anchor" href="#反应器飞温" aria-label="Permalink to &quot;反应器飞温&quot;">​</a></h3><blockquote><p><code>同反应器氢气中断</code></p></blockquote><h2 id="科目四-作业现场应急处置" tabindex="-1">科目四-作业现场应急处置 <a class="header-anchor" href="#科目四-作业现场应急处置" aria-label="Permalink to &quot;科目四-作业现场应急处置&quot;">​</a></h2><h3 id="反应器二段出口法兰泄漏着火有人受伤" tabindex="-1">反应器二段出口法兰泄漏着火有人受伤 <a class="header-anchor" href="#反应器二段出口法兰泄漏着火有人受伤" aria-label="Permalink to &quot;反应器二段出口法兰泄漏着火有人受伤&quot;">​</a></h3><h4 id="外操员" tabindex="-1">外操员 <a class="header-anchor" href="#外操员" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>外操员巡检，发现反应器二段出口法兰泄漏着火有人受伤，<strong>通过对讲机报告班长：&quot;反应器二段出口法兰泄漏着火有人受伤&quot;</strong></li><li>回到中控室，<strong>拿 F 型扳手和正压式空气呼吸器回到现场</strong></li></ol><h4 id="班长" tabindex="-1">班长 <a class="header-anchor" href="#班长" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过广播启动<code>车间泄漏着火应急预案</code></strong></li><li><strong>通过对讲机命令安全员：&quot;组织人员到 1 号门拉警戒线&quot;</strong></li><li><strong>通过中控室电话向调度报告：&quot;反应器二段出口法兰泄漏着火有人受伤，已启动应急预案&quot;</strong></li><li><strong>从中控室拿 F 型扳手和正压式空气呼吸器立即赶赴现场</strong></li><li><strong>通过对讲机命令外操员：&quot;使用消防炮对反应器进行降温控制&quot;</strong></li></ol><h4 id="外操员-1" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-1" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>打开消防炮对反应器进行降温控制</strong></li><li><strong>对中毒昏迷人员进行急救</strong></li></ol><h4 id="班长-1" tabindex="-1">班长 <a class="header-anchor" href="#班长-1" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过对讲机命令主操报火警 119</strong></li><li><strong>通过对讲机命令主操报急救 120</strong></li></ol><h4 id="主操" tabindex="-1">主操 <a class="header-anchor" href="#主操" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li><strong>拨打 119 报警电话：&quot;反应器二段出口法兰泄漏着火，火势较大，无法控制，请派消防车来灭火，报警人张三&quot;</strong></li><li><strong>拨打 120 报警电话：&quot;反应器二段出口法兰泄漏有人受伤，有人中毒昏迷，请派救护车来，报警人张三&quot;</strong></li></ol><h4 id="班长-2" tabindex="-1">班长 <a class="header-anchor" href="#班长-2" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过对讲机命令安全员：&quot;请组织人员到一号门引导消防车&quot;</strong></li><li><strong>通过对讲机命令安全员：&quot;请组织人员到一号门引导救护车&quot;</strong></li><li><strong>通过对讲机命令主操和外操员&quot;执行紧急停车操作&quot;</strong></li></ol><h4 id="主操中控执行紧急停车操作" tabindex="-1">主操中控执行紧急停车操作 <a class="header-anchor" href="#主操中控执行紧急停车操作" aria-label="Permalink to &quot;主操中控执行紧急停车操作&quot;">​</a></h4><ol><li>在中控室紧急操作平台，<strong>按动紧急停车按钮 HS1001</strong></li><li>在<code>进料换热系统</code>画面中，<strong>关闭氢气进一段调节阀 FIC1002 和 氢气进二段调节阀 FIC1003</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230612180356.png" alt="20230612180356"></li></ul></li><li>在<code>进料换热系统</code>画面中，<strong>将加热蒸汽去进料换热器 E-102 的温度调节阀 TIC1001 切为手动 MAN，OP 值先设置为 50，再设置为 0</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230612180716.png" alt="20230612180716"></li></ul></li><li>在<code>反应器部分</code>画面中，<strong>将反应器压力控制 PIC1001 切为手动 MAN，OP 值设置为 100</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230612180921.png" alt="20230612180921"></li></ul></li><li><strong>通过对讲机向班长报告：&quot;室内已按紧急停车处理完毕&quot;</strong></li></ol><h4 id="外操员现场执行紧急停车操作" tabindex="-1">外操员现场执行紧急停车操作 <a class="header-anchor" href="#外操员现场执行紧急停车操作" aria-label="Permalink to &quot;外操员现场执行紧急停车操作&quot;">​</a></h4><ol><li>关闭加热蒸汽去进料换热器 E-102 的温度调节阀(TV1001A)前阀</li><li>关闭反应物去 E105 的阀门 VX1R101</li><li>关闭氢气去一段调节阀 FV1002 的前阀</li><li>关闭氢气去二段调节阀 FV1003 的前阀</li><li>关闭原料去装置调节阀 FV1001 的前阀</li><li><strong>通过对讲机向班长报告：&quot;现场已按紧急停车处理完毕&quot;</strong></li></ol><h4 id="班长-3" tabindex="-1">班长 <a class="header-anchor" href="#班长-3" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>向调度室报告：&quot;事故处理完毕，可通知维修工对反应器泄漏的法兰进行检修&quot;</strong></li><li><strong>广播宣布：&quot;解除事故应急预案&quot;</strong></li></ol><h3 id="反应器一段入口阀门泄漏着火" tabindex="-1">反应器一段入口阀门泄漏着火 <a class="header-anchor" href="#反应器一段入口阀门泄漏着火" aria-label="Permalink to &quot;反应器一段入口阀门泄漏着火&quot;">​</a></h3><h4 id="外操员-2" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-2" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>外操员巡检，发现反应器二段出口法兰泄漏着火有人受伤，<strong>通过对讲机报告班长：&quot;反应器一段入口阀门泄漏着火&quot;</strong></li><li>回到中控室，<strong>拿 F 型扳手和正压式空气呼吸器回到现场</strong></li></ol><h4 id="班长-4" tabindex="-1">班长 <a class="header-anchor" href="#班长-4" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过广播启动<code>车间泄漏着火应急预案</code></strong></li><li><strong>通过对讲机命令安全员：&quot;组织人员到 1 号门拉警戒线&quot;</strong></li><li><strong>通过中控室电话向调度报告：&quot;反应器一段入口阀门泄漏着火，已启动应急预案&quot;</strong></li><li><strong>从中控室拿 F 型扳手和正压式空气呼吸器立即赶赴现场</strong></li><li><strong>通过对讲机命令外操员：&quot;使用消防炮对反应器进行降温控制&quot;</strong></li></ol><h4 id="外操员-3" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-3" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>打开消防炮对反应器进行降温控制</strong></li></ol><h4 id="班长-5" tabindex="-1">班长 <a class="header-anchor" href="#班长-5" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过对讲机命令主操报火警 119</strong></li></ol><h4 id="主操-1" tabindex="-1">主操 <a class="header-anchor" href="#主操-1" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li><strong>拨打 119 报警电话：&quot;反应器一段入口阀门泄漏着火，火势较大，无法控制，请派消防车来灭火，报警人张三&quot;</strong></li></ol><h4 id="班长-6" tabindex="-1">班长 <a class="header-anchor" href="#班长-6" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过对讲机命令安全员：&quot;请组织人员到一号门引导消防车&quot;</strong></li><li><strong>通过对讲机命令主操和外操员&quot;执行紧急停车操作&quot;</strong></li></ol><h4 id="主操中控执行紧急停车操作-1" tabindex="-1">主操中控执行紧急停车操作 <a class="header-anchor" href="#主操中控执行紧急停车操作-1" aria-label="Permalink to &quot;主操中控执行紧急停车操作&quot;">​</a></h4><ol><li>在中控室紧急操作平台，<strong>按动紧急停车按钮 HS1001</strong></li><li>在<code>进料换热系统</code>画面中，<strong>关闭氢气进一段调节阀 FIC1002 和 氢气进二段调节阀 FIC1003</strong></li><li>在<code>进料换热系统</code>画面中，<strong>将加热蒸汽去进料换热器 E-102 的温度调节阀 TIC1001 切为手动 MAN，OP 值先设置为 50，再设置为 0</strong></li><li>在<code>反应器部分</code>画面中，<strong>将反应器压力控制 PIC1001 切为手动 MAN，OP 值设置为 100</strong></li><li><strong>通过对讲机向班长报告：&quot;室内已按紧急停车处理完毕&quot;</strong></li></ol><h4 id="外操员现场执行紧急停车操作-1" tabindex="-1">外操员现场执行紧急停车操作 <a class="header-anchor" href="#外操员现场执行紧急停车操作-1" aria-label="Permalink to &quot;外操员现场执行紧急停车操作&quot;">​</a></h4><ol><li>关闭加热蒸汽去进料换热器 E-102 的温度调节阀(TV1001A)前阀</li><li>关闭反应物去 E105 的阀门 VX1R101</li><li>关闭氢气去一段调节阀 FV1002 的前阀</li><li>关闭氢气去二段调节阀 FV1003 的前阀</li><li>关闭原料去装置调节阀 FV1001 的前阀</li><li><strong>通过对讲机向班长报告：&quot;现场已按紧急停车处理完毕&quot;</strong></li></ol><h4 id="班长-7" tabindex="-1">班长 <a class="header-anchor" href="#班长-7" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>向调度室报告：&quot;事故处理完毕，可通知维修工对反应器泄漏的法兰进行检修&quot;</strong></li><li><strong>广播宣布：&quot;解除事故应急预案&quot;</strong></li></ol><h3 id="粗氢一段入口调节阀前阀泄漏中毒" tabindex="-1">粗氢一段入口调节阀前阀泄漏中毒 <a class="header-anchor" href="#粗氢一段入口调节阀前阀泄漏中毒" aria-label="Permalink to &quot;粗氢一段入口调节阀前阀泄漏中毒&quot;">​</a></h3><h4 id="外操员-4" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-4" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li>外操员巡检，<strong>通过对讲机报告班长：&quot;发现粗氢进一段反应器调节阀 FIC1004 入口法兰出泄漏，附近有一工人中毒晕倒在地&quot;</strong></li><li>回到中控室，<strong>拿 F 型扳手和正压式空气呼吸器回到现场</strong></li></ol><h4 id="班长-8" tabindex="-1">班长 <a class="header-anchor" href="#班长-8" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过广播启动<code>车间危险化学品泄漏应急预案</code></strong></li><li><strong>通过对讲机命令安全员：&quot;组织人员到 1 号门拉警戒线&quot;</strong></li><li><strong>通过中控室电话向调度报告：&quot;发现粗氢进一段反应器调节阀 FIC1004 入口法兰出泄漏，附近有一工人中毒晕倒在地，已启动应急预案&quot;</strong></li><li><strong>从中控室拿 F 型扳手和正压式空气呼吸器</strong></li><li><strong>通过对讲机命令外操员：&quot;立即去事故现场救人</strong></li><li><strong>通过对讲机命令主操拨打急救 120</strong></li></ol><h4 id="外操员-5" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-5" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>立即对受伤人员进行救护</strong></li></ol><h4 id="主操-2" tabindex="-1">主操 <a class="header-anchor" href="#主操-2" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li><strong>拨打 120 报警电话：&quot;固定床反应器单元粗氢入口阀门处，含一氧化碳的粗氢泄漏，有人中毒昏倒，请派救护车来，报警人张三&quot;</strong></li></ol><h4 id="班长-9" tabindex="-1">班长 <a class="header-anchor" href="#班长-9" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通过对讲机命令安全员：&quot;请组织人员到一号门引导救护车&quot;</strong></li><li><strong>通过对讲机命令外操员：&quot;检查泄漏点和泄漏情况&quot;</strong></li></ol><h4 id="外操员-6" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-6" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>通过对讲机向班长报告：&quot;发现粗氢进一段反应器调节阀 FIC1004 前阀入口处泄漏&quot;</strong></li></ol><h4 id="班长-10" tabindex="-1">班长 <a class="header-anchor" href="#班长-10" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>命令外操员：&quot;切换调节阀，FIC1004 旁路阀稍开，关闭粗氢进一段反应器调节阀前后手阀&quot;</strong></li><li><strong>命令主操：&quot;现场切换粗氢进一段反应器调节阀 FIC1004 旁路阀，请注意观察，并通知调度请仪表工进行调节阀检修&quot;</strong></li></ol><h4 id="外操员-7" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-7" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>稍开粗氢进一段反应器调节阀 FIC1004 的旁路阀</strong></li><li><strong>关闭调节阀前后手阀</strong></li><li><strong>报告班长：&quot;调节阀切换完毕&quot;</strong></li></ol><h4 id="主操-3" tabindex="-1">主操 <a class="header-anchor" href="#主操-3" aria-label="Permalink to &quot;主操&quot;">​</a></h4><ol><li><strong>通过中控室电话通知调度：&quot;请仪表工进行粗氢进一段反应器调节阀 FIC1004 检修&quot;</strong></li></ol><h4 id="外操员-8" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-8" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>通过对讲机报告班长：&quot;仪表工检修完毕&quot;</strong></li></ol><h4 id="班长-11" tabindex="-1">班长 <a class="header-anchor" href="#班长-11" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>通知外操员：&quot;现场将打开粗氢进一段反应器调节阀 FIC1004 前后阀&quot;</strong></li></ol><h4 id="外操员-9" tabindex="-1">外操员 <a class="header-anchor" href="#外操员-9" aria-label="Permalink to &quot;外操员&quot;">​</a></h4><ol><li><strong>通过对讲机告知主操：&quot;现场将打开粗氢进一段反应器调节阀 FIC1004 前后阀</strong></li><li><strong>打开调节阀后阀</strong></li><li><strong>打开调节阀前阀</strong></li><li><strong>关闭调节阀旁路阀</strong></li><li><strong>通过对讲机向班长报告：&quot;事故处理完毕&quot;</strong></li></ol><h4 id="班长-12" tabindex="-1">班长 <a class="header-anchor" href="#班长-12" aria-label="Permalink to &quot;班长&quot;">​</a></h4><ol><li><strong>向调度室报告：&quot;中毒工人已送往医院，泄漏点处理完毕，现生产恢复正常&quot;</strong></li><li><strong>广播宣布：&quot;解除事故应急预案&quot;</strong></li></ol>',95),r=[e];function n(s,h,d,u,c,g){return l(),a("div",null,r)}const x=t(i,[["render",n]]);export{b as __pageData,x as default};
