import{_ as a,c as e,o as l,V as t}from"./chunks/framework.399d643d.js";const p=JSON.parse('{"title":"每周一考-20230521","description":"","frontmatter":{},"headers":[],"relativePath":"One-week/20230521.md","lastUpdated":1686269911000}'),i={name:"One-week/20230521.md"},o=t('<h1 id="每周一考-20230521" tabindex="-1">每周一考-20230521 <a class="header-anchor" href="#每周一考-20230521" aria-label="Permalink to &quot;每周一考-20230521&quot;">​</a></h1><blockquote><p><strong>劳动防护用品的穿戴</strong></p></blockquote><h2 id="安全帽" tabindex="-1">安全帽 <a class="header-anchor" href="#安全帽" aria-label="Permalink to &quot;安全帽&quot;">​</a></h2><ol><li>应将内衬圆周大小调节到对头部稍有约束感，用双手试着左右转动头盔，以基本不能转动，但不难受的程度，以不系下领带低头时安全帽不会脱落为宜</li><li>安全帽由帽衬和帽壳组成，帽衬必须与帽壳连接良好，同时帽衬与帽壳不能紧贴，应有一定间隙，当有物体附落到安全帽壳上时，帽衬可起到缓冲作用不使颈椎受到伤害。</li><li>佩戴安全帽必须系好下领带，下领带应紧贴下领，松紧以下领有约束感,但不难受为宜(大概 1.5cm 左右)。</li><li>女生配戴安全帽应将头发放进帽衬。</li></ol><h2 id="工作服" tabindex="-1">工作服 <a class="header-anchor" href="#工作服" aria-label="Permalink to &quot;工作服&quot;">​</a></h2><ul><li>三紧，即领口紧、袖口紧、下摆紧。</li></ul><h2 id="安全带" tabindex="-1">安全带 <a class="header-anchor" href="#安全带" aria-label="Permalink to &quot;安全带&quot;">​</a></h2><ol><li>在没有防护设施的高处、悬崖、陡坡施工时，必须系好安全带。</li><li>安全带应高挂低用，注意防止摆动碰撞。若安全带低挂高用，一旦发生坠 落，将增加冲击力，带来危险。</li><li>安全绳的长度限制在 1.5~2m，使用 3m 以上长绳应加缓冲器。</li><li>不准将绳打结使用，也不准将钩直接挂在安全绳上使用，应挂在连接环上 用。</li><li>新使用的安全带必须有产品检验合格证，无证明不准使用。</li></ol>',8),r=[o];function n(s,c,_,d,h,u){return l(),e("div",null,r)}const f=a(i,[["render",n]]);export{p as __pageData,f as default};
