import{_ as e,o as t,c as a,V as o}from"./chunks/framework.6fc38dc5.js";const m=JSON.parse('{"title":"每周一考-20230618","description":"","frontmatter":{},"headers":[],"relativePath":"One-week/20230618.md","filePath":"One-week/20230618.md","lastUpdated":1689144091000}'),l={name:"One-week/20230618.md"},i=o('<h1 id="每周一考-20230618" tabindex="-1">每周一考-20230618 <a class="header-anchor" href="#每周一考-20230618" aria-label="Permalink to &quot;每周一考-20230618&quot;">​</a></h1><blockquote><p><strong>空气呼吸器的使用方法</strong></p></blockquote><ol><li>打开气瓶阀，检查气瓶气压(压力应在 28-30MPa)，检查气瓶的压力表指针应在绿色格之内，呼吸器各部件完好，然后关闭阀门，放尽余气</li><li>气瓶阀门和背托朝上，利用过肩式或交叉穿衣式背上呼吸器，适当调整肩带的上下位置和松紧，直到感觉舒适为止</li><li>插入腰带插头，然后将腰带一侧的伸缩带向后拉紧扣牢</li><li>撑开面罩头网，由上向下将面罩戴在头上，调整面罩位置。用手按住面罩进气口，通过吸气检查面罩密封是否良好，否则再收紧面罩紧固带，或重新戴面罩。</li><li>打开气瓶开关及供给阀</li><li>将供气阀接口与面罩接口吻合，然后握住面罩吸气根部，左手把供气阀向里按，当听到&quot;咔嚓&quot;声即安装完毕</li><li>应呼吸若干次检查供气阀性能。吸气和呼气都应舒畅，无不适感觉。</li><li>当空气呼吸器压力降至 5.0MPa 时应及时撤离更换气瓶</li></ol>',3),_=[i];function n(r,s,c,d,p,h){return t(),a("div",null,_)}const f=e(l,[["render",n]]);export{m as __pageData,f as default};
