import{_ as a,c as l,o as i,V as e}from"./chunks/framework.399d643d.js";const p=JSON.parse('{"title":"科目三-离心泵","description":"","frontmatter":{},"headers":[],"relativePath":"Oxidation-process/Hands-on-3-1.md","lastUpdated":1684477715000}'),o={name:"Oxidation-process/Hands-on-3-1.md"},t=e('<h1 id="科目三-离心泵" tabindex="-1">科目三-离心泵 <a class="header-anchor" href="#科目三-离心泵" aria-label="Permalink to &quot;科目三-离心泵&quot;">​</a></h1><h2 id="工艺介绍" tabindex="-1">工艺介绍 <a class="header-anchor" href="#工艺介绍" aria-label="Permalink to &quot;工艺介绍&quot;">​</a></h2><h3 id="工艺流程" tabindex="-1">工艺流程 <a class="header-anchor" href="#工艺流程" aria-label="Permalink to &quot;工艺流程&quot;">​</a></h3><h3 id="工艺设备" tabindex="-1">工艺设备 <a class="header-anchor" href="#工艺设备" aria-label="Permalink to &quot;工艺设备&quot;">​</a></h3><h2 id="实操项目" tabindex="-1">实操项目 <a class="header-anchor" href="#实操项目" aria-label="Permalink to &quot;实操项目&quot;">​</a></h2><h3 id="一、长时间停电" tabindex="-1">一、长时间停电 <a class="header-anchor" href="#一、长时间停电" aria-label="Permalink to &quot;一、长时间停电&quot;">​</a></h3><ol><li>关闭常用泵出口阀</li><li>出料流量调节阀改为手动 (MAN)</li><li>关闭出料流量调节阀，即控制开度 (OP) 为 0</li><li>关闭出料流量调节阀前后手阀</li><li>液位调节阀改为手动 (MAN)</li><li>关闭液位调节阀，即控制开度 (OP) 为 0</li><li>关闭液位调节阀前后手阀</li></ol><h3 id="二、原料泵坏" tabindex="-1">二、原料泵坏 <a class="header-anchor" href="#二、原料泵坏" aria-label="Permalink to &quot;二、原料泵坏&quot;">​</a></h3><ol><li>启用备用泵</li><li>在备用泵出口压力大于 0.72Mpa 的情况下打开其出口阀</li><li>关闭事故泵出口阀和入口阀</li><li>打开事故泵排液阀</li><li>出料流量调节阀改为手动 (MAN)，控制开度 (OP) 为 50</li><li>等待几秒，出口流量调节阀改为自动 (AUTO)，控制设定值 (SP) 为 20000</li><li>等待备用泵入口压力达到 0.475Mpa，出口压力达到 1.5Mpa，出口流量测量值 (PV) 控制在 20000Kg/h</li></ol><h3 id="三、原料泵抽空" tabindex="-1">三、原料泵抽空 <a class="header-anchor" href="#三、原料泵抽空" aria-label="Permalink to &quot;三、原料泵抽空&quot;">​</a></h3><ol><li>打开事故泵排气阀</li><li>出料流量调节阀改为手动 (MAN)，控制开度 (OP) 为 50</li><li>等待事故泵入口压力接近 0.475Mpa，出口压力接近 1.5Mpa</li><li>满足上述条件后，关闭事故泵排气阀</li></ol><h3 id="四、入口管线堵" tabindex="-1">四、入口管线堵 <a class="header-anchor" href="#四、入口管线堵" aria-label="Permalink to &quot;四、入口管线堵&quot;">​</a></h3><ol><li>启用备用泵</li><li>在备用泵出口压力达到 0.72Mpa 的情况下打开其出口阀</li><li>关闭事故泵出口阀</li><li>停用事故泵</li><li>关闭事故泵入口阀</li><li>出料流量调节阀改为手动 (MAN)，控制开度 (OP) 为 50</li><li>等待几秒，出口流量调节阀改为自动 (AUTO)，控制设定值 (SP) 为 20000</li><li>等待备用泵入口压力达到 0.475Mpa，出口压力达到 1.5Mpa，出口流量测量值 (PV) 控制在 20000Kg/h</li><li>在快速准确的完成上述步骤后，控制在考试限制时间前 1~2 分钟提交，利于获得高分</li></ol><h3 id="五、出料流量调节阀阀卡" tabindex="-1">五、出料流量调节阀阀卡 <a class="header-anchor" href="#五、出料流量调节阀阀卡" aria-label="Permalink to &quot;五、出料流量调节阀阀卡&quot;">​</a></h3><ol><li>打开出料流量调节阀旁路阀，控制开度为 50</li><li>关闭出料流量调节阀前后手阀</li><li>等待出口流量测量值 (PV) 达到 20000Kg/h</li><li>在快速准确的完成上述操作步骤后，控制在考试限制时间前 1~2 分钟提交，利于获得高分</li></ol>',15),r=[t];function h(n,d,s,c,_,u){return i(),l("div",null,r)}const b=a(o,[["render",h]]);export{p as __pageData,b as default};
