import{_ as t,c as e,o as l,V as i}from"./chunks/framework.399d643d.js";const p=JSON.parse('{"title":"科目三-离心压缩机","description":"","frontmatter":{},"headers":[],"relativePath":"Oxidation-process/Hands-on-3-2.md","lastUpdated":1684593681000}'),a={name:"Oxidation-process/Hands-on-3-2.md"},n=i('<h1 id="科目三-离心压缩机" tabindex="-1">科目三-离心压缩机 <a class="header-anchor" href="#科目三-离心压缩机" aria-label="Permalink to &quot;科目三-离心压缩机&quot;">​</a></h1><h2 id="工艺介绍" tabindex="-1">工艺介绍 <a class="header-anchor" href="#工艺介绍" aria-label="Permalink to &quot;工艺介绍&quot;">​</a></h2><h3 id="工艺流程" tabindex="-1">工艺流程 <a class="header-anchor" href="#工艺流程" aria-label="Permalink to &quot;工艺流程&quot;">​</a></h3><p>本套机组是由沈阳鼓风机厂制造的两段压缩机和杭州汽轮机厂制造的凝汽式汽轮机组成。机组主要用来将分馏塔顶油气分离来的富气(压力 0.18MPa，温度 40°C)，经透平压缩机机一段压缩后(压力 0.7MPa，温度 105°C)，进入中间冷却器冷却后进入中间分液罐进行分离。气相进入二段，继续升压后(压力 1.5Mpa，温度 105°C)，经二段出口送到稳定。 该机组在压缩气体的同时，担负着控制反应压力的任务。正常时，通过反应压力调节机组转速，达到控制反应压力的目的。另外，压缩机入口有两个 DN850 和 DN350 实行分程控制的放火炬阀，用来辅助调节反应压力。压缩机出口有一个反飞动阀，用来防止压缩机喘振。该套机组整个控制系统由 DCS 计算机系统执行。</p><h3 id="工艺设备" tabindex="-1">工艺设备 <a class="header-anchor" href="#工艺设备" aria-label="Permalink to &quot;工艺设备&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">C101A/B</td><td style="text-align:center;">压缩机</td><td style="text-align:center;">10</td><td style="text-align:center;">P102A/B</td><td style="text-align:center;">润滑油泵</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">K101</td><td style="text-align:center;">汽轮机</td><td style="text-align:center;">11</td><td style="text-align:center;">P103A/B</td><td style="text-align:center;">复水泵</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">D101</td><td style="text-align:center;">入口分液罐</td><td style="text-align:center;">12</td><td style="text-align:center;">S101A/B</td><td style="text-align:center;">润滑油过滤器</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">D102</td><td style="text-align:center;">气液分液罐</td><td style="text-align:center;">13</td><td style="text-align:center;">J101</td><td style="text-align:center;">油箱加热器</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">D103</td><td style="text-align:center;">润滑油箱</td><td style="text-align:center;">14</td><td style="text-align:center;">E101</td><td style="text-align:center;">气体级间冷却器</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">D104</td><td style="text-align:center;">高位油箱</td><td style="text-align:center;">15</td><td style="text-align:center;">E102</td><td style="text-align:center;">气体返回冷却器</td></tr><tr><td style="text-align:center;">07</td><td style="text-align:center;">D105A/B</td><td style="text-align:center;">储能器</td><td style="text-align:center;">16</td><td style="text-align:center;">E103A/B</td><td style="text-align:center;">润滑油冷却器</td></tr><tr><td style="text-align:center;">08</td><td style="text-align:center;">D106</td><td style="text-align:center;">复水器</td><td style="text-align:center;">17</td><td style="text-align:center;">E104</td><td style="text-align:center;">复水器表面冷却器</td></tr><tr><td style="text-align:center;">09</td><td style="text-align:center;">P101A/B</td><td style="text-align:center;">凝液泵</td><td style="text-align:center;">18</td><td style="text-align:center;">E105A/B</td><td style="text-align:center;">凝结水冷却器</td></tr></tbody></table><h3 id="流程简图" tabindex="-1">流程简图 <a class="header-anchor" href="#流程简图" aria-label="Permalink to &quot;流程简图&quot;">​</a></h3><h4 id="气压机系统" tabindex="-1">气压机系统 <a class="header-anchor" href="#气压机系统" aria-label="Permalink to &quot;气压机系统&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230520221750.png" alt="20230520221750"></p><h4 id="润滑油和密封气" tabindex="-1">润滑油和密封气 <a class="header-anchor" href="#润滑油和密封气" aria-label="Permalink to &quot;润滑油和密封气&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230520221856.png" alt="20230520221856"></p><h4 id="轴温轴振动监视" tabindex="-1">轴温轴振动监视 <a class="header-anchor" href="#轴温轴振动监视" aria-label="Permalink to &quot;轴温轴振动监视&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230520222047.png" alt="20230520222047"></p><h4 id="气压机组工艺系统详况画面" tabindex="-1">气压机组工艺系统详况画面 <a class="header-anchor" href="#气压机组工艺系统详况画面" aria-label="Permalink to &quot;气压机组工艺系统详况画面&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230520222359.png" alt="20230520222359"></p><h4 id="汽轮机启停逻辑" tabindex="-1">汽轮机启停逻辑 <a class="header-anchor" href="#汽轮机启停逻辑" aria-label="Permalink to &quot;汽轮机启停逻辑&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230520222644.png" alt="20230520222644"></p><h3 id="现场简图" tabindex="-1">现场简图 <a class="header-anchor" href="#现场简图" aria-label="Permalink to &quot;现场简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230520223949.png" alt="20230520223949"></p><h2 id="实操项目" tabindex="-1">实操项目 <a class="header-anchor" href="#实操项目" aria-label="Permalink to &quot;实操项目&quot;">​</a></h2><h3 id="一、长时间停电" tabindex="-1">一、长时间停电 <a class="header-anchor" href="#一、长时间停电" aria-label="Permalink to &quot;一、长时间停电&quot;">​</a></h3><ol><li>在 DCS 界面的<code>气压机启停逻辑</code>画面中，点击紧急停车按钮</li><li>在<code>气压机系统</code>关闭压缩机富气出口阀 (<strong>HIC1002</strong>)</li><li>在<code>气压机系统</code>关闭压缩机入口蝶阀 (<strong>HIC1001</strong>)</li><li>在现场二楼压缩机旁打开级间分液罐顶<code>安全阀 (SPV103) 的旁路阀 (SPV103B)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516093721.png" alt="找位置"></li></ul></li><li>关闭汽轮机<code>入口隔离阀 (VI2K101)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516094646.png" alt="找位置"></li></ul></li><li>关闭汽轮机入口<code>入口隔离阀 (VI2K101)</code><strong>前</strong><code>蒸汽放空阀 (VI3K101)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516095248.png" alt="找位置"></li></ul></li><li>关闭<code>轴封蒸汽阀门 (VI6K101)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516095600.png" alt="找位置"></li></ul></li><li>关闭<code>汽提蒸汽总阀 VI5K101</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516095800.png" alt="找位置"></li></ul></li><li>关闭<code>复水器二级汽抽入口阀 (VI1P105A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516100317.png" alt="找位置"></li></ul></li><li>关闭<code>二级汽抽蒸汽入口阀 (VI2P105A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516100843.png" alt="找位置"></li></ul></li><li>关闭<code>复水器一级汽抽入口阀 (VI1P106A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516101106.png" alt="找位置"></li></ul></li><li>关闭<code>一级汽抽蒸汽入口阀 (VI2P106A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516101726.png" alt="找位置"></li></ul></li><li>关闭<code>复水泵 (P103A) 的出口阀 (P103A0)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516102627.png" alt="找位置"></li></ul></li><li>关闭<code>凝液泵 (P101A) 出口阀 (P101A0)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516102842.png" alt="找位置"></li></ul></li></ol><h3 id="二、冷却水中断" tabindex="-1">二、冷却水中断 <a class="header-anchor" href="#二、冷却水中断" aria-label="Permalink to &quot;二、冷却水中断&quot;">​</a></h3><ol><li>在 DCS 界面的<code>气压机启停逻辑</code>画面中，点击紧急停车按钮</li><li>在<code>气压机系统</code>关闭压缩机富气出口阀 (<strong>HIC1002</strong>)</li><li>在<code>气压机系统</code>关闭压缩机入口蝶阀 (<strong>HIC1001</strong>)</li><li>在现场二楼压缩机旁打开级间分液罐顶<code>安全阀 (SPV103) 的旁路阀 (SPV103B)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516093721.png" alt="找位置"></li></ul></li><li>关闭汽轮机<code>入口隔离阀 (VI2K101)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516094646.png" alt="找位置"></li></ul></li><li>关闭汽轮机入口<code>入口隔离阀 (VI2K101)</code><strong>前</strong><code>蒸汽放空阀 (VI3K101)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516095248.png" alt="找位置"></li></ul></li><li>压缩机 C101 停下后进行机组盘车 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516111958.png" alt="找位置"></li></ul></li><li>关闭<code>轴封蒸汽阀门 (VI6K101)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516095600.png" alt="找位置"></li></ul></li><li>关闭<code>汽提蒸汽总阀 VI5K101</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516095800.png" alt="找位置"></li></ul></li><li>关闭<code>复水器二级汽抽入口阀 (VI1P105A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516100317.png" alt="找位置"></li></ul></li><li>关闭<code>二级汽抽蒸汽入口阀 (VI2P105A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516100843.png" alt="找位置"></li></ul></li><li>关闭<code>复水器一级汽抽入口阀 (VI1P106A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516101106.png" alt="找位置"></li></ul></li><li>关闭<code>一级汽抽蒸汽入口阀 (VI2P106A)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516101726.png" alt="找位置"></li></ul></li><li>停用<code>复水泵 (P103A)</code></li><li>关闭<code>复水泵 (P103A) 的出口阀 (P103A0)</code><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516102627.png" alt="找位置"></li></ul></li></ol><h3 id="三、润滑油油压低" tabindex="-1">三、润滑油油压低 <a class="header-anchor" href="#三、润滑油油压低" aria-label="Permalink to &quot;三、润滑油油压低&quot;">​</a></h3><ol><li>步骤 1 到步骤 15 与冷却水中断操作一致</li><li>步骤 16：关闭润滑油泵主泵 (P102A) 的<code>润滑油主泵蒸汽调节阀 (VX1P102)</code>和<code>润滑油主泵蒸汽出口阀 (VI1P102)</code><ul><li>如图所以位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230516113939.png" alt="找位置"></li></ul></li></ol><h3 id="四、复水器液位高" tabindex="-1">四、复水器液位高 <a class="header-anchor" href="#四、复水器液位高" aria-label="Permalink to &quot;四、复水器液位高&quot;">​</a></h3><ol><li>在 DCS 界面<code>气压机系统</code>中将<strong>自动</strong>状态的<strong>复水泵点击切换为手动</strong>，届时复水备用泵 (P103B) 启动</li><li>快速操作后，等待**复水器内液位 (LICA1005) 测量值 (PV) 降低到 55%**即可停止复水备用泵 (P103B) <ul><li>可以在现场观察液位 (LGD106) 来准确实现及时停止备用泵 (P103B)</li><li>可以在快速操作后通过 DCS 界面<code>气压机系统</code>中控制液位 (LICA1005) 调节阀的 SP 值为 55%来快速实现</li></ul></li><li>待<strong>复水器内液位 (LICA1005) 测量值 (PV) 控制在 50%左右</strong><ul><li>可以在快速操作后通过 DCS 界面<code>气压机系统</code>中控制液位 (LICA1005) 调节阀的 SP 值为 50%来快速实现</li></ul></li></ol>',28),d=[n];function c(r,s,o,h,g,u){return l(),e("div",null,d)}const x=t(a,[["render",c]]);export{p as __pageData,x as default};
