import{_ as t,c as l,o as i,V as a}from"./chunks/framework.399d643d.js";const q=JSON.parse('{"title":"科目三-换热器","description":"","frontmatter":{},"headers":[],"relativePath":"Oxidation-process/Hands-on-3-5.md","lastUpdated":1686287274000}'),n={name:"Oxidation-process/Hands-on-3-5.md"},e=a('<h1 id="科目三-换热器" tabindex="-1">科目三-换热器 <a class="header-anchor" href="#科目三-换热器" aria-label="Permalink to &quot;科目三-换热器&quot;">​</a></h1><h2 id="工艺介绍" tabindex="-1">工艺介绍 <a class="header-anchor" href="#工艺介绍" aria-label="Permalink to &quot;工艺介绍&quot;">​</a></h2><h3 id="工艺料程" tabindex="-1">工艺料程 <a class="header-anchor" href="#工艺料程" aria-label="Permalink to &quot;工艺料程&quot;">​</a></h3><p>冷物料 (92°C) 进入本单元，由泵 P101A/B，经调节器 FIC1001 控制流量送入换热器 E101 壳程，加热到 142°C(20%被汽化）后，经阀 VI2E101 出系统。热物料 (225°C) 进入系统，经泵 P102A/B，由温度调节器 TIC1001 分程控制主线调节阀 TV1001A 和副线调节阀 TV1001B （两调节阀的分程动作如下图所示） 使冷物料出口温度稳定；过主线阀 TV1001A 的热物料经换热器 E101 管程后，与副线阀 TV1001B 来的热物料混合 （混合温度为 (177±2°C) ) ，由阀 VI4E101 出本单元。</p><h3 id="工艺设备" tabindex="-1">工艺设备 <a class="header-anchor" href="#工艺设备" aria-label="Permalink to &quot;工艺设备&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">P101A/B</td><td style="text-align:center;">冷物料进料泵</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">P102A/B</td><td style="text-align:center;">热物料进料泵</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">E101</td><td style="text-align:center;">列管式换热器</td></tr></tbody></table><h3 id="料程简图" tabindex="-1">料程简图 <a class="header-anchor" href="#料程简图" aria-label="Permalink to &quot;料程简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609104320.png" alt="20230609104320"></p><h3 id="现场简图" tabindex="-1">现场简图 <a class="header-anchor" href="#现场简图" aria-label="Permalink to &quot;现场简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609104529.png" alt="20230609104529"></p><h2 id="实操项目" tabindex="-1">实操项目 <a class="header-anchor" href="#实操项目" aria-label="Permalink to &quot;实操项目&quot;">​</a></h2><h3 id="一、换热器结垢" tabindex="-1">一、换热器结垢 <a class="header-anchor" href="#一、换热器结垢" aria-label="Permalink to &quot;一、换热器结垢&quot;">​</a></h3><blockquote><p><strong>注意点:冷热物料的入口阀必须在出口压力降到 0.01Mpa 时才能关闭</strong></p></blockquote><h4 id="在现场" tabindex="-1">在现场 <a class="header-anchor" href="#在现场" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li>关闭<strong>热物料进料泵 P102A 的出口阀 P102A0</strong>，停用<strong>P102A 泵</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609114102.png" alt="20230609114102"></li></ul></li><li>当 P102A 出口压力降低到 0.01Mpa 时，关闭<strong>P102A 的入口阀 P102A1</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609114638.png" alt="20230609114638"></li></ul></li><li>关闭<strong>换热器 E101 的热物料出口阀 VI4E101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609115033.png" alt="20230609115033"></li></ul></li><li>全开<strong>换热器 E101 管程排气阀 VX2E101</strong><ul><li>如图所示位置 -<img src="http://qiniu.yeshan-taoist.cn/20230609115804.png" alt="20230609115804"></li></ul></li><li>打开<strong>换热器 E101 管程排液阀 RPY</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609120848.png" alt="20230609120848"></li></ul></li><li>打开<strong>换热器 E101 管程排凝校验阀 VI5E101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609121152.png" alt="20230609121152"></li></ul></li><li>确认<strong>换热器 E101 管程排凝校验阀 VI5E101 导淋无液体流出后</strong>，关闭<strong>换热器 E101 管程排气阀 VX2E101</strong>和<strong>换热器 E101 管程排液阀 RPY</strong><ul><li>该操作可跳过先去操作其他，等处理完后再回来处理</li></ul></li><li>关闭<strong>冷物料进料泵 P101A 的出口阀 P101A0</strong>，停用<strong>P101A 泵</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609122421.png" alt="20230609122421"></li></ul></li><li>当 P101A 出口压力降到 0.01Mpa 时，关闭<strong>P101A 的入口阀 P101A1</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609122658.png" alt="20230609122658"></li></ul></li><li>关闭<strong>换热器 E101 的冷物料入口阀 VI1E101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609122829.png" alt="20230609122829"></li></ul></li><li>关闭<strong>换热器 E101 的冷物料出口阀 VI2E101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609123115.png" alt="20230609123115"></li></ul></li><li>全开<strong>换热器 E101 壳程排气阀 VX1E101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609123303.png" alt="20230609123303"></li></ul></li><li>打开<strong>换热器 E101 壳程泄液阀 LPY</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609123514.png" alt="20230609123514"></li></ul></li><li>打开<strong>换热器 E101 壳程排凝校验阀 VI3E101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609123634.png" alt="20230609123634"></li></ul></li><li>确认<strong>换热器 E101 壳程排凝校验阀 VI3E101 导淋无液体流出后</strong>，关闭<strong>换热器 E101 壳程排气阀 VX1E101</strong>和<strong>换热器 E101 壳程排液阀 LPY</strong><ul><li>该操作可跳过先去操作其他，等处理完后再回来处理</li></ul></li></ol><h4 id="在-dcs-画面中" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230609124124.png" alt="20230609124124"></p><ol><li>将 TIC1001 切换到手动 MAN，OP 值设置为 50，等待几秒后，OP 值设置为 0</li><li>将 FIC1001 切换到手动 MAN，OP 值设置为 0</li></ol><h3 id="二、热物料泵坏" tabindex="-1">二、热物料泵坏 <a class="header-anchor" href="#二、热物料泵坏" aria-label="Permalink to &quot;二、热物料泵坏&quot;">​</a></h3><ol><li>切换热物料进料泵备用泵 P102B <ul><li>先开泵</li><li>等待几秒后打开泵出口阀</li></ul></li><li>关闭常用泵 P102A 出口阀和入口阀</li><li>等待各工艺指标回复正常，在临近截止时间前 30 秒提交</li></ol><h3 id="三、冷物料泵坏" tabindex="-1">三、冷物料泵坏 <a class="header-anchor" href="#三、冷物料泵坏" aria-label="Permalink to &quot;三、冷物料泵坏&quot;">​</a></h3><ol><li>切换冷物料进料泵备用泵 P101B <ul><li>先开泵</li><li>等待几秒后打开泵出口阀</li></ul></li><li>关闭常用泵 P101A 出口阀和入口阀</li><li>等待各工艺指标回复正常，在临近截止时间前 30 秒提交</li></ol><h3 id="四、长时间停电" tabindex="-1">四、长时间停电 <a class="header-anchor" href="#四、长时间停电" aria-label="Permalink to &quot;四、长时间停电&quot;">​</a></h3><h3 id="五、冷物料中断" tabindex="-1">五、冷物料中断 <a class="header-anchor" href="#五、冷物料中断" aria-label="Permalink to &quot;五、冷物料中断&quot;">​</a></h3>',24),r=[e];function s(o,h,c,g,d,u){return i(),l("div",null,r)}const P=t(n,[["render",s]]);export{q as __pageData,P as default};
