import{_ as t,c as e,o as a,V as r}from"./chunks/framework.399d643d.js";const _=JSON.parse('{"title":"科目三-精馏塔","description":"","frontmatter":{},"headers":[],"relativePath":"Oxidation-process/Hands-on-3-6.md","lastUpdated":1686289098000}'),n={name:"Oxidation-process/Hands-on-3-6.md"},l=r('<h1 id="科目三-精馏塔" tabindex="-1">科目三-精馏塔 <a class="header-anchor" href="#科目三-精馏塔" aria-label="Permalink to &quot;科目三-精馏塔&quot;">​</a></h1><h2 id="工艺介绍" tabindex="-1">工艺介绍 <a class="header-anchor" href="#工艺介绍" aria-label="Permalink to &quot;工艺介绍&quot;">​</a></h2><p>本单元是一种加压精馏操作 73°C 的原料液经流量 FIC1001 控制流量后，从精馏塔 T101 的第 16 块塔板进料。塔顶蒸气经塔顶全凝器冷凝为液体以后进入回流罐 D101;回流罐 D101 的液体由泵 P101A/B 抽出，一部分作为回流液由调节器 FIC1004 控流量控制送回 T101 塔顶:另一部分则作为产品，其流量由调节器 FIC1003 控制。回流罐的液位由调节器 LIC1003 和 FIC1003 构成的串级控制回路控制。T101 操作压力由调节器 PIC1002 分程控制为 0.41MpaG，同时调节器 PIC1001 将调节回流罐的气相出料，保证系统的安全和稳定 塔釜液体的一部分经再沸器 E102A/B 回精馏塔，另一部分由调节器 FIC1002 控制流量，作为塔低采出产品。调节器 LIC1001 和 FIC1002 构成串级控制回路，调节精馏塔的液位。再沸器用低压蒸汽加热，加热蒸汽流量由调节器 TIC1001 控制，其冷凝液送 D102。D102 的液位由调节器 LIC1002 调节。</p><h3 id="工艺流程" tabindex="-1">工艺流程 <a class="header-anchor" href="#工艺流程" aria-label="Permalink to &quot;工艺流程&quot;">​</a></h3><h3 id="工艺设备" tabindex="-1">工艺设备 <a class="header-anchor" href="#工艺设备" aria-label="Permalink to &quot;工艺设备&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">T101</td><td style="text-align:center;">精馏塔</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">E101</td><td style="text-align:center;">精馏塔塔顶冷凝器</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">D101</td><td style="text-align:center;">精馏塔塔顶回流罐</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">P101A/B</td><td style="text-align:center;">回流液输送泵</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">E102</td><td style="text-align:center;">精馏塔塔釜再沸器</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">D102</td><td style="text-align:center;">精馏塔塔釜蒸汽缓冲罐</td></tr></tbody></table><h3 id="流程简图" tabindex="-1">流程简图 <a class="header-anchor" href="#流程简图" aria-label="Permalink to &quot;流程简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609133601.png" alt="20230609133601"></p><h3 id="现场简图" tabindex="-1">现场简图 <a class="header-anchor" href="#现场简图" aria-label="Permalink to &quot;现场简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609133707.png" alt="20230609133707"></p><h2 id="实操项目" tabindex="-1">实操项目 <a class="header-anchor" href="#实操项目" aria-label="Permalink to &quot;实操项目&quot;">​</a></h2><h3 id="一、冷却水供应不足" tabindex="-1">一、冷却水供应不足 <a class="header-anchor" href="#一、冷却水供应不足" aria-label="Permalink to &quot;一、冷却水供应不足&quot;">​</a></h3><h3 id="二、长时间停电" tabindex="-1">二、长时间停电 <a class="header-anchor" href="#二、长时间停电" aria-label="Permalink to &quot;二、长时间停电&quot;">​</a></h3><h3 id="三、原料中断事故" tabindex="-1">三、原料中断事故 <a class="header-anchor" href="#三、原料中断事故" aria-label="Permalink to &quot;三、原料中断事故&quot;">​</a></h3><h3 id="四、加热蒸汽中断" tabindex="-1">四、加热蒸汽中断 <a class="header-anchor" href="#四、加热蒸汽中断" aria-label="Permalink to &quot;四、加热蒸汽中断&quot;">​</a></h3><h3 id="五、回流中断" tabindex="-1">五、回流中断 <a class="header-anchor" href="#五、回流中断" aria-label="Permalink to &quot;五、回流中断&quot;">​</a></h3>',16),d=[l];function i(o,s,h,c,x,u){return a(),e("div",null,d)}const g=t(n,[["render",i]]);export{_ as __pageData,g as default};
