import{_ as t,o as e,c as a,V as l}from"./chunks/framework.6fc38dc5.js";const q=JSON.parse('{"title":"科目三-固定床反应器","description":"","frontmatter":{},"headers":[],"relativePath":"OxidationProcess/Hands-on-3-4.md","filePath":"OxidationProcess/Hands-on-3-4.md","lastUpdated":null}'),i={name:"OxidationProcess/Hands-on-3-4.md"},n=l('<h1 id="科目三-固定床反应器" tabindex="-1">科目三-固定床反应器 <a class="header-anchor" href="#科目三-固定床反应器" aria-label="Permalink to &quot;科目三-固定床反应器&quot;">​</a></h1><h2 id="工艺介绍" tabindex="-1">工艺介绍 <a class="header-anchor" href="#工艺介绍" aria-label="Permalink to &quot;工艺介绍&quot;">​</a></h2><h3 id="工艺流程" tabindex="-1">工艺流程 <a class="header-anchor" href="#工艺流程" aria-label="Permalink to &quot;工艺流程&quot;">​</a></h3><ul><li>本单元模拟的乙炔加氢反应系统，其作用是除去脱乙烷塔顶气相混合碳二组分中的乙炔。</li><li>脱乙烷塔塔顶物料经加热后进入乙炔转化器 (R101A/B)采用选择性加氢生成乙烯的方法，除去物料中所含的乙炔。本装置设有两台反应器，进行切换操作，催化剂在不影响连续操作的情况下，用过热蒸汽和空气的混合物对催化剂进行再生</li><li>该加氢步骤分两步完成以提高反应的选择性。反应都是在气相中进行的。通过流量控制将氢气干燥器的高纯度 (95% (mol) ) 气加入到反应器进料中。通过流量控制还加入少量粗氢 (含有 CO) 以调节催化剂的活性。高纯度氢和粗还通过流量控制加入到二段反应器进料中。反应器进料首先通过与乙炔转化器进料/排放物流换热器(E101)中的反应器排放物流换热，然后与乙炔转化器进料预热器 E102 中的低压蒸汽换热被加热到反应温度。然后，它进入第一个反应器床层，并向下流经催化剂床层。在第一个床层中应有大约 75%的乙炔被转化。一段的排放物流在乙炔转化器中间冷却器中被冷却以脱除反应的热量，然后进入第二个反应器床层，对剩余的乙炔进行加氢反应。</li></ul><h3 id="工艺设备" tabindex="-1">工艺设备 <a class="header-anchor" href="#工艺设备" aria-label="Permalink to &quot;工艺设备&quot;">​</a></h3><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">位号</th><th style="text-align:center;">名称</th></tr></thead><tbody><tr><td style="text-align:center;">01</td><td style="text-align:center;">E101</td><td style="text-align:center;">进出料换热器</td></tr><tr><td style="text-align:center;">02</td><td style="text-align:center;">E102</td><td style="text-align:center;">进料加热器</td></tr><tr><td style="text-align:center;">03</td><td style="text-align:center;">E103</td><td style="text-align:center;">R101A/B 中间冷却器</td></tr><tr><td style="text-align:center;">04</td><td style="text-align:center;">E104</td><td style="text-align:center;">R102A/B 中间冷却器</td></tr><tr><td style="text-align:center;">05</td><td style="text-align:center;">E105</td><td style="text-align:center;">出料冷却器</td></tr><tr><td style="text-align:center;">06</td><td style="text-align:center;">R101A/B</td><td style="text-align:center;">乙炔转化器</td></tr><tr><td style="text-align:center;">07</td><td style="text-align:center;">R102A/B</td><td style="text-align:center;">乙炔转化器</td></tr><tr><td style="text-align:center;">08</td><td style="text-align:center;">P102A/B</td><td style="text-align:center;">原料进料泵</td></tr><tr><td style="text-align:center;">09</td><td style="text-align:center;">D101</td><td style="text-align:center;">凝液罐</td></tr></tbody></table><h3 id="流程简图" tabindex="-1">流程简图 <a class="header-anchor" href="#流程简图" aria-label="Permalink to &quot;流程简图&quot;">​</a></h3><h4 id="进料换热系统" tabindex="-1">进料换热系统 <a class="header-anchor" href="#进料换热系统" aria-label="Permalink to &quot;进料换热系统&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230609084835.png" alt="20230609084835"></p><h4 id="反应器部分" tabindex="-1">反应器部分 <a class="header-anchor" href="#反应器部分" aria-label="Permalink to &quot;反应器部分&quot;">​</a></h4><p><img src="http://qiniu.yeshan-taoist.cn/20230609084929.png" alt="20230609084929"></p><h3 id="现场简图" tabindex="-1">现场简图 <a class="header-anchor" href="#现场简图" aria-label="Permalink to &quot;现场简图&quot;">​</a></h3><p><img src="http://qiniu.yeshan-taoist.cn/20230609085232.png" alt="20230609085232"></p><h2 id="实操项目" tabindex="-1">实操项目 <a class="header-anchor" href="#实操项目" aria-label="Permalink to &quot;实操项目&quot;">​</a></h2><h3 id="一、反应器氢气中断" tabindex="-1">一、反应器氢气中断 <a class="header-anchor" href="#一、反应器氢气中断" aria-label="Permalink to &quot;一、反应器氢气中断&quot;">​</a></h3><blockquote><p><strong>反应器泄压和凝液罐泄液完毕提交</strong></p></blockquote><h4 id="在-dcs-画面中" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><ol><li>在<code>进料换热系统</code>画面中，<strong>切断一段反应器氢气进料 FIC1002、切断二段反应器氢气进料 FIC1003、切断烃进料 FIC1001、关闭氢气进料切断阀 UV1002、关闭物料去产品出装置的切断阀 UV1003</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093309.png" alt="20230609093309"></li></ul></li><li>在<code>进料换热系统</code>画面中，打开<strong>凝液罐液位调节阀 LIC1001</strong>，切换到手动，OP 值设置为 100 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609094432.png" alt="20230609094432"></li></ul></li><li>在<code>反应器部分</code>画面中，打开<strong>反应器压力调节阀 PIC1001</strong>，切换到手动，OP 值设置为 100 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609094717.png" alt="20230609094717"></li></ul></li></ol><h4 id="在现场" tabindex="-1">在现场 <a class="header-anchor" href="#在现场" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li>关闭<strong>蒸汽进料前阀 TV1001AI</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609092826.png" alt="20230609092826"></li></ul></li><li>打开产品<strong>不合格油出装置阀门 VI4R101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093108.png" alt="20230609093108"></li></ul></li><li>打开 R101 二楼的<strong>R101A 的排绿阀 VX1R101A</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093521.png" alt="20230609093521"></li></ul></li><li>打开 R101 一楼的<strong>R101B 的排绿阀 VX1R101B</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093742.png" alt="20230609093742"></li></ul></li></ol><h3 id="二、冷却水中断" tabindex="-1">二、冷却水中断 <a class="header-anchor" href="#二、冷却水中断" aria-label="Permalink to &quot;二、冷却水中断&quot;">​</a></h3><blockquote><p><strong>反应器泄压完毕提交</strong></p></blockquote><h4 id="在-dcs-画面中-1" tabindex="-1">在 DCS 画面中 <a class="header-anchor" href="#在-dcs-画面中-1" aria-label="Permalink to &quot;在 DCS 画面中&quot;">​</a></h4><ol><li>在<code>进料换热系统</code>画面中，<strong>切断一段反应器氢气进料 FIC1002、切断二段反应器氢气进料 FIC1003、切断烃进料 FIC1001、关闭物料去产品出装置的切断阀 UV1003</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609100302.png" alt="20230609100302"></li></ul></li><li>在<code>反应器部分</code>画面中，打开<strong>反应器压力调节阀 PIC1001</strong>，切换到手动，OP 值设置为 100 <ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609094717.png" alt="20230609094717"></li></ul></li></ol><h4 id="在现场-1" tabindex="-1">在现场 <a class="header-anchor" href="#在现场-1" aria-label="Permalink to &quot;在现场&quot;">​</a></h4><ol><li>打开产品<strong>不合格油出装置阀门 VI4R101</strong><ul><li>如图所示位置</li><li><img src="http://qiniu.yeshan-taoist.cn/20230609093108.png" alt="20230609093108"></li></ul></li></ol><h3 id="三、反应器飞温" tabindex="-1">三、反应器飞温 <a class="header-anchor" href="#三、反应器飞温" aria-label="Permalink to &quot;三、反应器飞温&quot;">​</a></h3><blockquote><p><strong>同反应器氢气中断</strong></p></blockquote>',28),r=[n];function o(s,d,c,h,g,u){return e(),a("div",null,r)}const p=t(i,[["render",o]]);export{q as __pageData,p as default};
